when validating parameters in {{cite document}} templates, the basic and
]]
local document_arguments_t = { ['bibcode'] = true, ['bibcode-access'] = true, ['doi'] = true, ['DOI'] = true, ['doi-access'] = true, ['doi-broken-date'] = true, ['hdl'] = true, ['HDL'] = true, ['hdl-access'] = true, ['id'] = true, ['ID'] = true, ['jfm'] = true, ['JFM'] = true, ['lang'] = true, ['location'] = true, ['mr'] = true, ['MR'] = true, ['no-pp'] = true, ['orig-date'] = true, ['origyear'] = true, ['orig-year'] = true, ['osti'] = true, ['OSTI'] = true, ['osti-access'] = true, ['place'] = true, ['publisher'] = true, ['quote-page'] = true, ['quote-pages'] = true, ['script-quote'] = true, ['script-title'] = true, ['title-link'] = true, ['translator'] = true, ['translator-first'] = true, ['translator-given'] = true, ['translator-last'] = true, ['translator-surname'] = true, ['translator-link'] = true, ['translator-mask'] = true, ['trans-quote'] = true, ['type'] = true, ['zbl'] = true, ['ZBL'] = true, }
local document_numbered_arguments_t = { ['translator#'] = true, ['translator-first#'] = true, ['translator#-first'] = true, ['translator-given#'] = true, ['translator#-given'] = true, ['translator-last#'] = true, ['translator#-last'] = true, ['translator-surname#'] = true, ['translator#-surname'] = true, ['translator-link#'] = true, ['translator#-link'] = true, ['translator-mask#'] = true, ['translator#-mask'] = true, }
--[[--------------------------< L I S T _ C O M B I N E >------------------------------------------------------
makes one table from a list of tables. <lists_t> is a sequence of tables to be combined
]]
local function list_combine (lists_t) local out_t = {};
for _, list_t in ipairs (lists_t) do -- for each list in <lists_t> for k, v in pairs (list_t) do -- extract each k/v pair out_t[k] = v; -- add to <out_t> end end return out_t; -- and done end
--[[--------------------------< T E M P L A T E _ L I S T _ G E T >--------------------------------------------
gets a list of the templates from table t
]]
local function template_list_get (t) local out_t = {}; -- a table for output for k, _ in pairs (t) do -- spin through the table and collect the keys table.insert (out_t, k) -- add each key to the output table end return out_t; -- and done end
--[[--------------------------< E X P O R T E D T A B L E S >------------------------------------------------ ]]
return { preprint_arguments_t = preprint_arguments_t, preprint_template_list_t = template_list_get (preprint_arguments_t), -- make a template list from preprint_arguments{} table unique_arguments_t = unique_arguments_t, unique_param_template_list_t = template_list_get (unique_arguments_t), -- make a template list from unique_arguments{} table
document_parameters_t = list_combine ({limited_basic_arguments_t, limited_numbered_arguments_t, document_arguments_t, document_numbered_arguments_t}); common_parameters_t = list_combine ({basic_arguments_t, numbered_arguments_t}); limited_parameters_t = list_combine ({limited_basic_arguments_t, limited_numbered_arguments_t}); };

