local function setupSortkeys(t) local retval = {} for k, v in ipairs(t) do retval[v[2]] = { name = v[1], sortkey = k } end return retval end
return { reviewers = setupSortkeys(reviewers), aggregators = setupSortkeys(aggregators), systems = setupSortkeys(systems), i18n = { wrapper = 'Template:Video game reviews', templatestyles = 'Module:Video game reviews/styles.css', pattern = { reviewer = '^rev%d+$', aggregator = '^agg%d+$', award = '^award%d+$', }, class = { aggregators = 'vgr-aggregators', awards = 'vgr-awards', centeredpub = 'vgr-center', container = 'video-game-reviews', containerleft = 'vgr-left', containernone = 'vgr-none', containersingle = 'vgr-single', dark = 'skin-invert', headerrow = 'vgr-hrow', na = 'table-na', -- same as Template:n/a reviews = 'vgr-reviews', stacked = 'vgr-stacked', subtitle = 'vgr-subtitle', title = 'vgr-title', wikidata = 'vgr-edit-on-wikidata', -- we keep wikitable around even though all the styles are overriden -- because it helps some scripts to know to look for a real data table wikitable = 'wikitable', }, state = { autocollapse = 'autocollapse', -- you may not have this on your wiki collapsed = 'collapsed', expanded = 'expanded', }, align = { left = 'left', none = 'none', }, category = { empty = '[[Category:Empty templates on articles]]', multiplatform = '[[Category:Articles using Video game reviews template in multiple platform mode]]', singleplatform = '[[Category:Articles using Video game reviews template in single platform mode]]', }, display = { aggregateScore = 'Aggregate score', aggregateScores = 'Aggregate scores', aggregator = 'Aggregator', award = 'Award', awards = 'Awards', na = 'N/A', publication = 'Publication', reception = 'Reception', reviewScore = 'Review score', reviewScores = 'Review scores', score = 'Score', }, }, argi18n = { -- For non-English wikis, add translations of argument keys here. -- Example: -- subtitle = 'untertitel' }, }

