-- ['ast-leo'] = true, -- deleted; use ast-es -- ['lmo-cr'] = true, -- deleted; use lmo-x-cremish -- ['lmo-it'] = true, -- deleted; use lmo-x-berg -- ['pun'] = true, -- deleted; was a template dab -- ['sa2'] = true, -- deleted; use {{lang}}
-- ['bcs-latn-cyrl'] = true, -- {{lang-x2}} template wrappers; these tags not valid -- ['cnr-cyrl-latn'] = true, -- ['cnr-latn-cyrl'] = true, -- ['sh-cyrl-latn'] = true, -- ['sh-latn-cyrl'] = true, -- ['sr-cyrl-latn'] = true, -- ['sr-latn-cyrl'] = true, -- ['uz-cyrl-latn'] = true, -- ['uz-latn-cyrl'] = true,
-- ['cnr-cyrl'] = true, -- valid IETF tags; no reason to prevent their use with {{langx}} -- ['cnr-latn'] = true, -- ['hmd'] = true, -- ['ka'] = true, -- ['ku-arab'] = true, -- ['mnc'] = true, -- ['my-mymr'] = true, -- ['rus'] = true, -- sort of valid IETF tag; should be promoted to 'ru' -- ['sh-cyrl'] = true, -- ['sh-latn'] = true, -- ['sr'] = true, -- per Template_talk:Lang#tracking_sr_usage_with_issues; discussion died nothing being done -- ['sr-cyrl'] = true, -- ['sr-latn'] = true, -- ['uz-latn'] = true, -- ['zh'] = true,
